mapper.select:
SELECT A.*,B.totalCnt FROM ( SELECT tt1.post_no, tt1.rnum as no, tt1.title, tt1.contents, --당일 등록: 시:분, 그렇치 않으면 년-월-일 DECODE(TO_CHAR(tt1.reg_dt,'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD') ,TO_CHAR(tt1.reg_dt,'HH24:MI') ,TO_CHAR(tt1.reg_dt,'YYYY-MM-DD')) as regDt, tt1.id FROM ( SELECT ROWNUM rnum, T1.* FROM ( SELECT * FROM qa --WHERE 검색 WHERE 1=1
ORDER BY reg_dt DESC )T1 --10,20,30,... WHERE ROWNUM <=#{pageSize} * (#{pageNo}-1) + #{pageSize} )TT1 --1,11,21,31,.. WHERE rnum >= #{pageSize} * (#{pageNo}-1) + 1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM qa --WHERE 검색 WHERE 1=1
)B
