mapper.update:


UPDATE bulletin
   SET read_cnt = NVL(read_cnt,0)+1
 WHERE post_no  = #{postNo}
   AND id <> #{id}

mapper.select:
SELECT A.*,B.totalCnt FROM ( SELECT tt1.rnum as postNo, tt1.title, tt1.contents, DECODE(TO_CHAR(tt1.reg_dt,'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD') ,TO_CHAR(tt1.reg_dt,'HH24:MI') ,TO_CHAR(tt1.reg_dt,'YYYY-MM-DD')) as regDt, --당일 등록: 시:분, 그렇지 않으면 년-월-일 DECODE(TO_CHAR(tt1.mod_dt,'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD') ,TO_CHAR(tt1.mod_dt,'HH24:MI') ,TO_CHAR(tt1.mod_dt,'YYYY-MM-DD')) as modDt, tt1.read_cnt as readCnt, tt1.id, tt1.mod_id as modId FROM ( SELECT ROWNUM rnum, T1.* FROM ( SELECT * FROM bulletin --WHERE 검색 WHERE 1=1
ORDER BY mod_dt DESC )T1 --10,20,30,... WHERE ROWNUM <=#{pageSize} * (#{pageNo}-1) + #{pageSize} )TT1 --1,11,21,31,.. WHERE rnum >= #{pageSize} * (#{pageNo}-1) + 1 )A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM bulletin --WHERE 검색 WHERE 1=1
)B

mapper.update:
UPDATE bulletin
SET
title = #{title}
,contents = #{contents}
,mod_dt = SYSDATE
,mod_id = #{modId}
WHERE post_no = #{postNo}
